    fun uniffi_connlib_checksum_method_disconnecterror_to_string(): Short

    fun uniffi_connlib_checksum_method_protectsocket_protect_socket(): Short

    fun uniffi_connlib_checksum_method_session_disconnect(): Short

    fun uniffi_connlib_checksum_method_session_reset(): Short

    fun uniffi_connlib_checksum_method_session_set_disabled_resources(): Short

